using static FullInspector.Internal.fiLateBindings;

namespace Oddments.Scripts.Items.Guns
{
    public class ClipModifierExampleGun : GunBehaviour
    {
        public static GunTemplate gunTemplate = new GunTemplate(typeof(ClipModifierExampleGun))
        {
            Name = "Red Coiled",
            PostInitAction = gun =>
            {
                ProjectileModule module = gun.AddProjectileModuleFrom("ak-47", true, false);
                gun.DefaultModule.shootStyle = ProjectileModule.ShootStyle.Automatic;
                gun.DefaultModule.angleVariance = 5f;
                gun.DefaultModule.cooldownTime = 0.1f;
                gun.DefaultModule.numberOfShotsInClip = 15;
                Gun gun2 = PickupObjectDatabase.GetById(151) as Gun;
                gun.muzzleFlashEffects = gun2.muzzleFlashEffects;
                gun.DefaultModule.ammoCost = 1;

                Projectile projectile = Instantiate(module.projectiles[0]);
                projectile.gameObject.SetActive(false);
                FakePrefab.MarkAsFakePrefab(projectile.gameObject);
                UnityEngine.Object.DontDestroyOnLoad(projectile);
                projectile.baseData.damage = 5;

                //var modiHolder = JuneLib.GunClipModifierHolder.Initialize(gun, null);

                var projVolley = ScriptableObject.CreateInstance<ProjectileVolleyData>();
                var projMod = ProjectileModule.CreateClone(((Gun)PickupObjectDatabase.GetById(207)).DefaultModule);
                projMod.shootStyle = ProjectileModule.ShootStyle.Automatic;
                
                projVolley.projectiles = new List<ProjectileModule>() { projMod };

                var modifier = ClipModifierBase.Initialize<ExampleClipModifier>("SkibidiRizz");
                modifier.volley = projVolley;
                GunClipModifierHolder.AddInnateStaticModifier(modifier);
                //modiHolder.GetModifier(gun.DefaultModule.runtimeGuid).AddClipModifierToGun(modifier, true);
               // modiHolder.importantThing = "Booger nuts";
                //ETGModConsole.Log(modiHolder.modifiers.Count);
                //

                ETGModConsole.Log("Everything ran as it should");
                gun.RemovePickupFromLootTables();
            },
            InfAmmo = false,
            MaxAmmo = 300,
        };

        public class ExampleClipModifier : ClipModifierBase
        {
            public ProjectileVolleyData volley;
            public override void AddVolleys(GunClipModifiers.RuntimeModifierContainer modifier, Gun gun, PlayerController player, ProjectileModule baseModule)
            {
                for (int i = 0; i < 5; i++)
                {
                    int offset = 3 + i * 3;
                    for (int j = 0; j < 2; j++)
                    {
                        modifier.InsertData(offset, volley);
                    }
                }
            }
        }
    }
}
